import React from 'react';
import classnames from 'classnames';
import { Formik, Form, Field } from 'formik';
import styles from './App.css';

function validateEmail(value) {
  if (!value) {
    return 'Required';
  }
  else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
    return 'invalid email address';
  }
}
function validatePassword(value) {
  if (!value) { return 'Required'; }
}
console.log('ddd');
function App() {
  return (
    <React.Fragment>
      <div>XXX</div>
      <div className="App">
        <Formik
          initialValues={{ email: "", password: "" }}
          onSubmit={values => {
            console.log('submit', values)
          }}
        >
          {(errors, touched) => {
            <Form>
              <label className={classnames(
                styles.label, { [styles.errorLabel]: errors.email && touched.email }
              )}
              >
                e-mail
              </label>
              <Field className={classnames(
                styles.field, { [styles.errorInput]: errors.email && touched.email }
              )}
                name="email"
                validate={validateEmail}
              />
              <label className={classnames(
                styles.label, { [styles.errorLabel]: errors.password && touched.password }
              )}
              >
                pwd
              </label>
              <Field className={classnames(
                styles.field, { [styles.errorInput]: errors.password && touched.password }
              )}
                name="password"
                validate={validatePassword}
              />
              {errors.password && touched.password && (
                <div className={styles.error}>{errors.password}</div>
              )}
              <button type="submit">Submit</button>
            </Form>
          }}

        </Formik>
      </div>
    </React.Fragment>)
}
export default App;
